import styled from 'styled-components';

export const Container = styled.div`
    width: 100vw; /* 뷰포트 너비에 맞춤 */
    height: 645px;
    position: relative;
    overflow: hidden;
    background: #fff;
`;

export const StyledImage = styled.img`
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    object-fit: cover;
`;
