import { styled } from 'styled-components';

export const Container = styled.div`
    position: relative;
    width: 100vw;
    height: ${(props) => props.height || '80vh'};
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;

    @media (max-width: 768px) {
        height: ${(props) => props.heightSmall || '60vh'};
    }
`;

export const BackgroundImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
`;

export const Overlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 2;
`;

export const TextContainer = styled.div`
    position: relative;
    z-index: 3;
    text-align: center;
    color: #fff;
`;

export const LogoBox = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    border: 3px solid #fff;
    padding: 20px;
    position: relative;

    @media (max-width: 768px) {
        padding: 10px;
    }
`;

export const Line = styled.div`
    position: absolute;
    top: -3px;
    left: -3px;
    width: 30px;
    height: 30px;
    border-top: 3px solid #fff;
    border-left: 3px solid #fff;
`;

export const Title = styled.h2`
    font-size: 3rem;
    font-weight: 150;
    margin: 0;
    padding: 0;
    text-align: center;
    color: #fff;
    font-family: 'Noto Sans KR', sans-serif;

    & strong {
        font-weight: 700;
    }

    @media (max-width: 768px) {
        font-size: 2rem;
    }
`;

export const Subtitle = styled.h3`
    font-size: 24px;
    font-weight: 400;
    margin: 0;
    padding: 0;
    margin-top: 10px;
    text-align: center;
    color: #fff;
    font-family: 'Noto Sans KR', sans-serif;

    @media (max-width: 768px) {
        font-size: 18px;
    }
`;
