import styled from 'styled-components';
import DaonWayImage from '../../img/DaonWay.png';
import { FaBus, FaSubway } from 'react-icons/fa';

const Container = styled.div`
    max-width: 900px;
    margin: 0 auto;
    padding: 20px;
    text-align: center;
`;

const Title = styled.h1`
    font-size: 2rem;
    margin-bottom: 20px;
    color: #2947a9;
`;

const MapContainer = styled.div`
    width: 100%;
    height: 400px;
    margin-bottom: 20px;
    border-radius: 10px;
    overflow: hidden;
`;

const Image = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 10px;
`;

const AddressContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
    font-size: 1.2em;
    color: #555;
`;

const AddressText = styled.div`
    display: flex;
    align-items: center;
    svg {
        margin-right: 10px;
        color: #e74c3c;
    }
`;

const TransportContainer = styled.div`
    display: flex;
    justify-content: center;
    margin-top: 40px;
    text-align: left;
    font-size: 1.2em;
    color: #555;
`;

const TransportItem = styled.div`
    display: flex;
    align-items: center;
    margin: 0 20px;

    svg {
        margin-right: 10px;
        color: #f9995d;
    }
`;

export default function WaytoComePage() {
    return (
        <Container>
            <Title>오시는 길</Title>
            <MapContainer>
                <Image src={DaonWayImage} alt="Daon Tech 위치" />
            </MapContainer>
            <AddressContainer>
                <AddressText>
                    <FaBus />
                    경기도 안산시 단원구 원포공원1로 59 신명트윈타워 A동 5층
                </AddressText>
            </AddressContainer>
            <TransportContainer>
                <TransportItem>
                    <FaBus />
                    새마을운동중앙회 하차
                </TransportItem>
                <TransportItem>
                    <FaSubway />
                    삼성역 2번 출구 도보로 15분, 학여울역 1번 출구 도보로 10분
                </TransportItem>
            </TransportContainer>
        </Container>
    );
}
