import styled from 'styled-components';
import DaonWayImage from '../../img/DaonWay.png';

const Container = styled.div`
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
    text-align: center;
`;

const Address = styled.a`
    margin-top: 40px;
    text-align: left;
    font-size: 1.2em;
    line-height: 1.6;
    color: #555;
    text-decoration: none;
    display: block;

    &:hover {
        text-decoration: underline;
        color: #2947a9;
    }
`;

const Image = styled.img`
    width: 100%;
    height: auto;
    margin-top: 20px;
    border-radius: 10px;
`;

export default function WaytoComePage() {
    const naverMapUrl =
        'https://map.naver.com/v5/directions/-/-/-/transit?c=14130292.5551395,4489650.3300299,15,0,0,0,dh';

    return (
        <Container>
            <Image src={DaonWayImage} alt="Daon Tech 위치" />
            <Address href={naverMapUrl} target="_blank" rel="noopener noreferrer">
                주소: 경기도 안산시 단원구 원포공원1로 59 신명트윈타워 A동 5층
            </Address>
        </Container>
    );
}
