import React, { useEffect } from 'react';
import styled from 'styled-components';
import { FaPhoneAlt, FaEnvelope, FaBus, FaSubway } from 'react-icons/fa';

const Container = styled.div`
    max-width: 900px;
    margin: 0 auto;
    padding: 20px;
    text-align: center;
`;

const MapContainer = styled.div`
    width: 100%;
    height: 400px;
    margin-bottom: 20px;
    border-radius: 10px;
    overflow: hidden;
`;

const InfoContainer = styled.div`
    margin-top: 20px;
    text-align: left;
    font-size: 1.2em;
    line-height: 1.6;
    color: #555;
`;

const InfoItem = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #2947a9;
    }
`;

const InfoText = styled.span`
    font-size: 1em;
    color: #555;
`;

const TransportContainer = styled.div`
    margin-top: 40px;
    text-align: left;
`;

const TransportItem = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #f9995d;
    }
`;

const TransportText = styled.span`
    font-size: 1em;
    color: #555;
`;

export default function WaytoComePage() {
    useEffect(() => {
        const mapContainer = document.getElementById('map');
        const mapOption = {
            center: new kakao.maps.LatLng(37.4850307, 126.8825114), // 좌표 설정
            level: 3, // 지도 확대 레벨
        };

        const map = new kakao.maps.Map(mapContainer, mapOption);

        const markerPosition = new kakao.maps.LatLng(37.4850307, 126.8825114);
        const marker = new kakao.maps.Marker({
            position: markerPosition,
        });
        marker.setMap(map);
    }, []);

    return (
        <Container>
            <h1>오시는 길</h1>
            <MapContainer id="map"></MapContainer>
            <InfoContainer>
                <InfoItem>
                    <FaPhoneAlt />
                    <InfoText>대표번호: 010-5219-8234</InfoText>
                </InfoItem>
                <InfoItem>
                    <FaEnvelope />
                    <InfoText>이메일: sfs230@hanmail.net</InfoText>
                </InfoItem>
            </InfoContainer>
            <TransportContainer>
                <h2>교통편</h2>
                <TransportItem>
                    <FaBus />
                    <TransportText>버스: 새마을운동중앙회 하차</TransportText>
                </TransportItem>
                <TransportItem>
                    <FaSubway />
                    <TransportText>지하철: 삼성역 2번 출구 도보로 15분, 학여울역 1번 출구 도보로 10분</TransportText>
                </TransportItem>
            </TransportContainer>
        </Container>
    );
}
