import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    justify-content: space-around;
    padding: 5vh 10vw; /* 반응형 패딩 */
    background-color: #f8f8f8;
`;

export const Card = styled.div`
    position: relative;
    width: 45%; /* 직사각형 형태를 위해 폭을 늘립니다. */
    overflow: hidden;
    border-radius: 10px;
    cursor: pointer;
    transition: transform 0.3s ease-in-out;

    &:hover img {
        transform: scale(1.1); /* 이미지 확대 */
        opacity: 1; /* 확대 시 불투명하게 */
    }
`;

export const CardImage = styled.img`
    width: 100%;
    height: 20vh; /* 고정 높이 대신 상대적 높이 사용 */
    display: block;
    transition: transform 0.3s ease-in-out, opacity 0.3s ease-in-out; /* 부드러운 확대와 불투명도 전환 */
    opacity: 0.95; /* 기본적으로 흐리게 */
`;

export const CardTitle = styled.h3`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    font-size: 2rem; /* 글씨 크기 증가 */
    font-weight: bold; /* 글씨를 더 진하게 */
    text-align: center;
    white-space: nowrap;
    z-index: 1; /* 글씨를 이미지 위에 표시 */
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.7); /* 글씨에 그림자 추가 */
`;
