import styled from 'styled-components';

const Container = styled.div`
    width: 100vw; /* 뷰포트 너비에 맞춤 */
    height: 645px;
    position: relative;
    overflow: hidden;
    background: #fff;
    z-index: 1; /* Lower z-index to ensure it stays behind */
`;

export const StyledImage = styled.img`
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    object-fit: cover;
`;
