import React from 'react';
import styled from 'styled-components';
// import backgroundImage from '../assets/background-image.jpeg'; // 이미지 경로를 적절히 수정하세요.

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background-image: url(${backgroundImage});
    background-size: cover;
    background-position: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: #fff;
    text-align: center;
    padding: 20px;
`;

const Title = styled.h1`
    font-size: 3rem;
    margin-bottom: 20px;
`;

const SubTitle = styled.h2`
    font-size: 1.5rem;
    margin-bottom: 40px;
`;

const InfoWrapper = styled.div`
    display: flex;
    justify-content: space-around;
    width: 100%;
    max-width: 1000px;
`;

const InfoBox = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    font-size: 1.2rem;
`;

const Highlight = styled.span`
    font-size: 2rem;
    font-weight: bold;
    margin-bottom: 10px;
`;

export default function MainSection() {
    return (
        <Container>
            <Title>Now, JEIO</Title>
            <SubTitle>제이오는 누구도 가보지 않은 혁신의 영역을 끊임없이 개척합니다.</SubTitle>
            <InfoWrapper>
                <InfoBox>
                    <Highlight>No.1</Highlight>
                    <p>최고의 기술력과 서비스</p>
                </InfoBox>
                <InfoBox>
                    <Highlight>21년</Highlight>
                    <p>탄소나노튜브 연구개발</p>
                </InfoBox>
                <InfoBox>
                    <Highlight>1,000건 이상</Highlight>
                    <p>플랜트 EPC 프로젝트 수행</p>
                </InfoBox>
            </InfoWrapper>
        </Container>
    );
}
