import styled from 'styled-components';

export const Container = styled.div`
    max-width: 1800px;
    margin: 0 auto;
    padding: 20px;
`;

export const Title = styled.h1`
    font-size: 2rem;
    font-weight: bold;
`;

export const SubTitle = styled.p`
    font-size: 1.2rem;
    margin-bottom: 20px;
`;

export const Form = styled.form`
    display: flex;
    flex-direction: column;
`;

export const Input = styled.input`
    padding: 10px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
    font-size: 1rem;
`;

export const TextArea = styled.textarea`
    padding: 10px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
    font-size: 1rem;
    resize: vertical;
`;

export const CheckboxWrapper = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;
`;

export const Checkbox = styled.input`
    margin-right: 10px;
`;

export const PrivacyPolicy = styled.div`
    border: 1px solid #ccc;
    padding: 10px;
    margin-bottom: 20px;
    font-size: 0.9rem;
    line-height: 1.5;
`;

export const Button = styled.button`
    padding: 10px;
    background-color: #333;
    color: white;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;

    &:hover {
        background-color: #555;
    }
`;
