import { Outlet, useLocation } from 'react-router-dom';
import Header from '../screens/Component/HeaderComponet';
import Bottom from '../screens/Component/BottomComponent';
import { createGlobalStyle } from 'styled-components';
import styled from 'styled-components'; // styled 정의 추가
import { CSSTransition, SwitchTransition } from 'react-transition-group';
import '../screens/css/animations.css'; // 애니메이션 스타일 임포트

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box; 
}
`;

const AnimationContainer = styled.div`
    width: 100%; // 필요에 따라 조정
    height: 100%; // 필요에 따라 조정
`;

function Root() {
    const location = useLocation();

    return (
        <>
            <GlobalStyle />
            <AnimationContainer>
                <Header />
                <SwitchTransition>
                    <CSSTransition key={location.key} classNames="fade" timeout={300}>
                        <Outlet />
                    </CSSTransition>
                </SwitchTransition>
                <Bottom />
            </AnimationContainer>
        </>
    );
}

export default Root;
