import styled from 'styled-components';

export const Container = styled.div`
    position: relative;
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
`;

export const BackgroundImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
`;

export const Overlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5); /* 투명도 조절 */
    z-index: 2;
`;

export const TextContainer = styled.div`
    position: relative;
    z-index: 3;
    text-align: center;
    color: #fff;
`;

export const Title = styled.h1`
    font-size: 48px;
    font-weight: 700;
    margin: 0;
    padding: 0;
`;

export const Subtitle = styled.h2`
    font-size: 24px;
    font-weight: 400;
    margin: 0;
    padding: 0;
    margin-top: 20px;
`;
