import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.div`
    width: 100vw;
    height: 80px;
    position: relative;
    overflow: hidden;
    background: #fff;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 0 80px;
`;

export const LogoSection = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px; /* 로고 섹션과 네비게이션 섹션 간의 간격 조정 */
`;

export const Nav = styled.div`
    display: flex;
    align-items: center;
    gap: 50px;
`;

export const NavItem = styled(Link)`
    flex-grow: 0;
    flex-shrink: 0;
    font-size: 18px;
    text-align: left;
    color: #14171f;
    text-decoration: none;
    cursor: pointer;
`;

export const SpecialNavItem = styled.a`
    flex-grow: 0;
    flex-shrink: 0;
    font-size: 18px;
    text-align: left;
    color: #f9995d;
    font-weight: 600;
    text-decoration: none;
    cursor: pointer;
`;

export const Logo = styled(Link)`
    font-size: 1.5rem;
    font-weight: 1000;
    font-style: italic;
    text-align: left;
    color: #2947a9;
    text-decoration: none;
    margin-left: 8px;
`;
