import React from 'react';
import styled from 'styled-components';
import DaonWayImage from '../../img/DaonWay.png';
import { FaMapMarkerAlt, FaBus, FaSubway } from 'react-icons/fa';

const Container = styled.div`
    max-width: 100vw; /* 너비를 더 넓게 설정 */
    margin: 0 auto;
    padding: 20px;
    text-align: center;
`;

const Title = styled.h1`
    font-size: 2rem;
    margin-bottom: 20px;
    color: #2947a9;
`;

const MapContainer = styled.div`
    width: 100%;
    height: 400px;
    margin-bottom: 20px;
    border-radius: 10px;
    overflow: hidden;
`;

const Image = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 10px;
`;

const InfoContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center; /* 세로 중앙 정렬 추가 */
    margin-top: 20px;
    text-align: left;
    font-size: 1.2em;
    line-height: 1.6;
    color: #555;
`;

const AddressContainer = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    margin-right: 20px;

    svg {
        margin-right: 10px;
        color: #e74c3c;
    }
`;

const Divider = styled.div`
    width: 1px;
    height: 50px; /* 높이를 줄여서 아이콘 높이와 일치시키기 */
    background-color: #ccc;
    margin: 0 20px;
`;

const TransportContainer = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
`;

const TransportTitle = styled.h2`
    font-size: 1.5em;
    margin-bottom: 10px;
    color: #333;
`;

const TransportItem = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #f9995d;
    }
`;

export default function WaytoComePage() {
    return (
        <Container>
            <Title>오시는 길</Title>
            <MapContainer>
                <Image src={DaonWayImage} alt="Daon Tech 위치" />
            </MapContainer>
            <InfoContainer>
                <AddressContainer>
                    <FaMapMarkerAlt />
                    <span>경기도 안산시 단원구 원포공원1로 59 신명트윈타워 A동 5층</span>
                </AddressContainer>
                <Divider />
                <TransportContainer>
                    <TransportTitle>교통편</TransportTitle>
                    <TransportItem>
                        <FaBus />
                        <span>새마을운동중앙회 하차</span>
                    </TransportItem>
                    <TransportItem>
                        <FaSubway />
                        <span>삼성역 2번 출구 도보로 15분, 학여울역 1번 출구 도보로 10분</span>
                    </TransportItem>
                </TransportContainer>
            </InfoContainer>
        </Container>
    );
}
