import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.div`
    width: 100vw;
    height: 80px;
    position: relative;
    overflow: hidden;
    background: #fff;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 80px;
    border-bottom: 2px solid #e0e3eb; /* 밑줄 추가 */
`;

export const LogoSection = styled.div`
    display: flex;
    align-items: center;
`;

export const NavWrapper = styled.div`
    display: flex;
    justify-content: center;
`;

export const Nav = styled.div`
    display: flex;
    align-items: center;
    gap: 50px;
`;

export const NavItem = styled.div`
    position: relative;
    &:hover > ul {
        display: block;
    }
`;

export const DropdownMenu = styled.ul`
    display: none;
    position: absolute;
    top: 100%;
    left: 0;
    background: #fff;
    padding: 10px 0;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    list-style: none;
    border: 1px solid #e0e3eb;
    border-radius: 4px;
    z-index: 1000;
`;

export const StyledLink = styled(Link)`
    display: block;
    padding: 8px 20px;
    color: #333;
    text-decoration: none;
    &:hover {
        background: #f5f5f5;
    }
`;

export const SpecialNavItem = styled.a`
    flex-grow: 0;
    flex-shrink: 0;
    font-size: 18px;
    text-align: left;
    color: #f9995d;
    font-weight: 600;
    text-decoration: none;
    cursor: pointer;
`;

export const Logo = styled(Link)`
    font-size: 1.5rem;
    font-weight: 1000;
    font-style: italic;
    text-align: left;
    color: #2947a9;
    text-decoration: none;
    margin-left: 8px;
`;
