import styled from 'styled-components';

export const Container = styled.div`
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
    text-align: center;
`;

export const Title = styled.h1`
    font-size: 2em;
    margin-bottom: 20px;
    color: #333;
`;

export const Content = styled.p`
    font-size: 1.2em;
    line-height: 1.6;
    margin-bottom: 20px;
    text-align: left;
    color: #555;
`;
