import React, { useState } from 'react';
import {
    Container,
    Title,
    SubTitle,
    Form,
    Input,
    TextArea,
    CheckboxWrapper,
    Checkbox,
    PrivacyPolicy,
    Button,
} from '../styled/OnlineInquiryStyles';

const OnlineInquiry = () => {
    const [isChecked, setIsChecked] = useState(false);
    const [isPolicyVisible, setIsPolicyVisible] = useState(false);

    const handleCheckboxChange = () => {
        setIsChecked(!isChecked);
    };

    const handlePolicyClick = () => {
        setIsPolicyVisible(!isPolicyVisible);
    };

    return (
        <Container>
            <Title>온라인문의</Title>
            <SubTitle>끊임없는 기술개발과 투자를 통해 기업의 지속가능 경영을 실현합니다.</SubTitle>
            <Form>
                <Input type="text" placeholder="이름을 입력하세요." />
                <Input type="text" placeholder="연락처를 입력하세요." />
                <Input type="email" placeholder="이메일을 입력하세요." />
                <TextArea rows="4" placeholder="내용을 입력하세요." />
                <CheckboxWrapper>
                    <Checkbox type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
                    <span>개인정보처리방침에 동의합니다.</span>
                    <button
                        type="button"
                        onClick={handlePolicyClick}
                        style={{
                            marginLeft: '10px',
                            background: 'none',
                            border: 'none',
                            color: 'blue',
                            cursor: 'pointer',
                        }}
                    >
                        개인정보처리방침 보기
                    </button>
                </CheckboxWrapper>
                {isPolicyVisible && (
                    <PrivacyPolicy>
                        1. 수집하는 개인정보 항목 이름, 이메일주소, 연락처(전화번호)
                        <br />
                        2. 수집 및 이용목적 고객상담 처리 및 결과회신 등
                        <br />
                        3. 보유 및 이용기간 개인정보 수집 및 이용목적이 달성된 후에는 해당 정보를 지체 없이 파기합니다.
                        단, 관련 법령의 규정에 의하여 보존할 의무가 있는 경우 법령이 지정한 일정기간 동안 보관합니다.
                        <br />
                        개인정보의 수집 및 이용에 관한 사항에 동의하십니까?
                    </PrivacyPolicy>
                )}
                <Button type="submit">확인</Button>
            </Form>
        </Container>
    );
};

export default OnlineInquiry;
