import styled from 'styled-components';

export const Container = styled.div`
    width: 70vw;
    margin: 0 auto;
    padding: 80px 80px 50px 80px;
    background-color: #fff;
    text-align: center;
`;

export const Title = styled.h1`
    font-size: 3rem;
    color: #000;
    text-align: center;
    margin-bottom: 20px;
`;

export const MapAndInfoWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    gap: 20px;
`;

export const InfoContainer = styled.div`
    flex: 1;
    background-color: #2c3e50;
    color: white;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

export const MapContainer = styled.div`
    flex: 1;
    height: 550px;
    border-radius: 10px;
    overflow: hidden;
`;

export const AddressContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin-bottom: 20px;
    font-size: 1.2rem;

    svg {
        margin-right: 10px;
        color: #e74c3c;
    }
`;

export const TransportContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
`;

export const TransportTitle = styled.h2`
    font-size: 1.5rem;
    margin-bottom: 10px;
    color: #f9995d;
`;

export const TransportItem = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;
    font-size: 1.2rem;

    svg {
        margin-right: 10px;
        color: #f9995d;
    }
`;

export const ContactItem = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #ecf0f1;
    }
`;
