import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.div`
    width: 80vw;
    margin: 0 auto;
    padding: 120px 0;
    background-color: #fff;
    text-align: center;
`;

export const Title = styled.h1`
    font-size: 3rem;
    color: #000;
    text-align: center;
    margin-bottom: 20px;
`;

export const MapAndInfoWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    gap: 20px;
`;

export const InfoContainer = styled.div`
    flex: 1;
    height: 70vh;
    background-color: #2c3e50;
    color: white;
    padding: 40px;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    text-align: left;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    gap: 20px;
`;

export const MapContainer = styled.div`
    flex: 1;
    height: 40vh;
    border-radius: 10px;
    overflow: hidden;
`;

export const AddressContainer = styled.div`
    display: flex;
    align-items: center;
    font-size: 1.2rem;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #e74c3c;
    }
`;

export const ContactItem = styled.div`
    display: flex;
    align-items: center;
    font-size: 1.2rem;
    margin-bottom: 10px;

    svg {
        margin-right: 10px;
        color: #ecf0f1;
    }
`;

export const ButtonWrapper = styled.div`
    margin-top: auto;
    text-align: center; /* 버튼이 중앙에 위치하도록 수정 */
`;

export const Button = styled(Link)`
    padding: 10px 30px; /* 버튼의 패딩을 조정하여 크기를 키움 */
    background-color: #3498db;
    color: white;
    border: 2px solid white;
    border-radius: 4px;
    font-size: 1.2rem; /* 폰트 크기 조정 */
    font-weight: bold;
    cursor: pointer;
    text-decoration: none;
    display: inline-block; /* 버튼을 인라인 블록 요소로 설정 */

    &:hover {
        background-color: #2980b9;
    }

    &::after {
        content: ' +';
        margin-left: 10px;
    }
`;
